;let ealert=new function(){let class_name="ealert",id="ealert-"+Math.floor((Math.random()*100)+Math.random()),container_class_name="ealert_container";this.position="top right";this.duration=3000;this.appearance="default";this.fire=function(a,b){let main_class=class_name+" "+a;let div=document.createElement("div");div.setAttribute("class",container_class_name);document.body.appendChild(div);let alert_div=document.createElement("div");alert_div.setAttribute("class",main_class+" "+this.appearance+" "+this.position);alert_div.id=id;div.append(alert_div);let icon_div=document.createElement("div");icon_div.setAttribute("class","icon");alert_div.append(icon_div);let message_div=document.createElement("div");message_div.setAttribute("class","message");message_div.innerHTML=b;alert_div.append(message_div);setTimeout(function(){alert_div.classList.add("ealert_hide");setTimeout(function(){document.body.removeChild(div)},1000)},this.duration)}};